import java.util.*;
import java.lang.*;

class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();

        long count = 0;
        List<Integer> path = new ArrayList<>();
        dfs(n, m, 1, path, new boolean[n + 1][n + 1], count);

        System.out.println(count);

    }

    static void dfs(int n, int m, int current, List<Integer> path, boolean[][] adj, long count) {

        if (path.size() == m) {
            if (isConnected(n, adj)) {
                count++;
            }
            return;

        }

        for (int i = 1; i <= n; i++) {
            List<Integer> newPath = new ArrayList<>(path);
            newPath.add(i);
            boolean[][] newAdj = copyAdj(adj);
            if (current != i) {
                newAdj[current][i] = true;
            }
            dfs(n, m, i, newPath, newAdj, count);
        }

    }


    static boolean isConnected(int n, boolean[][] adj) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                if (i == j) continue;
                if (!canReach(i, j, n, adj)) return false;
            }
        }
        return true;
    }

    static boolean canReach(int start, int end, int n, boolean[][] adj) {
        boolean[] visited = new boolean[n + 1];
        Queue<Integer> q = new LinkedList<>();
        q.offer(start);
        visited[start] = true;
        while (!q.isEmpty()) {
            int curr = q.poll();
            if (curr == end) return true;
            for (int i = 1; i <= n; i++) {
                if (adj[curr][i] && !visited[i]) {
                    visited[i] = true;
                    q.offer(i);
                }
            }
        }
        return false;
    }

    static boolean[][] copyAdj(boolean[][] adj) {
        int n = adj.length;
        boolean[][] newAdj = new boolean[n][n];
        for (int i = 0; i < n; i++) {
            System.arraycopy(adj[i], 0, newAdj[i], 0, n);
        }
        return newAdj;
    }
}

