public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int q = sc.nextInt();
        List<long[]> edges = new ArrayList<>();
        for (int i = 0; i < q; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            long c = sc.nextLong();
            for (int j = 0; j < n; j++) {
                edges.add(new long[]{(a + j) % n, (b + j) % n, c + j});
            }
        }
        Collections.sort(edges, (e1, e2) -> Long.compare(e1[2], e2[2]));
        UnionFind uf = new UnionFind(n);
        long totalWeight = 0;
        for (long[] edge : edges) {
            if (uf.union((int) edge[0], (int) edge[1])) {
                totalWeight += edge[2];
            }
        }
        System.out.println(totalWeight);
    }

    static class UnionFind {
        int[] parent;
        int[] rank;

        UnionFind(int n) {
            parent = new int[n];
            rank = new int[n];
            for (int i = 0; i < n; i++) {
                parent[i] = i;
            }
        }

        int find(int x) {
            if (parent[x] == x) {
                return x;
            }
            return parent[x] = find(parent[x]);
        }

        boolean union(int x, int y) {
            int rootX = find(x);
            int rootY = find(y);
            if (rootX != rootY) {
                if (rank[rootX] < rank[rootY]) {
                    parent[rootX] = rootY;
                } else if (rank[rootX] > rank[rootY]) {
                    parent[rootY] = rootX;
                } else {
                    parent[rootY] = rootX;
                    rank[rootX]++;
                }
                return true;
            }
            return false;
        }
    }
}

import sys

def solve():
    n, q = map(int, sys.stdin.readline().split())
    edges = []
    for _ in range(q):
        a, b, c = map(int, sys.stdin.readline().split())
        i = 0
        while True:
            a_cur = (a + i) % n
            b_cur = (b + i) % n
            edges.append((a_cur, b_cur, c + i))
            i += 1
            if i > 2 * n:
              break

    parent = list(range(n))
    rank = [0] * n

    def find(i):
        if parent[i] == i:
            return i
        parent[i] = find(parent[i])
        return parent[i]

    def union(i, j, weight):
        root_i = find(i)
        root_j = find(j)
        if root_i != root_j:
            if rank[root_i] < rank[root_j]:
                parent[root_i] = root_j
            elif rank[root_i] > rank[root_j]:
                parent[root_j] = root_i
            else:
                parent[root_j] = root_i
                rank[root_i] += 1
            return weight
        return 0

    edges.sort(key=lambda x: x[2])
    total_weight = 0
    for u, v, weight in edges:
        total_weight += union(u, v, weight)

    print(total_weight)

