using namespace std;

int main() {
  int h, w;
  cin >> h >> w;

  vector<string> grid(h);
  for (int i = 0; i < h; ++i) {
    cin >> grid[i];
  }

  set<vector<string>> distinct_grids;
  distinct_grids.insert(grid);

  for (int i = 0; i < (1 << (h + w)); ++i) {
    vector<string> current_grid = grid;
    for (int j = 0; j < h + w; ++j) {
      if ((i >> j) & 1) {
        if (j < h) {
          reverse(current_grid[j].begin(), current_grid[j].end());
        } else {
          int col = j - h;
          for (int k = 0; k < h / 2; ++k) {
            swap(current_grid[k][col], current_grid[h - 1 - k][col]);
          }
        }
      }
    }
    distinct_grids.insert(current_grid);
  }

  cout << distinct_grids.size() << endl;
  return 0;
}

import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int H = sc.nextInt();
        int W = sc.nextInt();
        char[][] grid = new char[H][W];
        for (int i = 0; i < H; i++) {
            String s = sc.next();
            for (int j = 0; j < W; j++) {
                grid[i][j] = s.charAt(j);
            }
        }

        Set<String> states = new HashSet<>();
        Queue<Pair> q = new LinkedList<>();
        q.offer(new Pair(grid));
        states.add(encode(grid));

        while (!q.isEmpty()) {
            Pair p = q.poll();
            char[][] cur = p.grid;

            // Row reverse
            for (int i = 0; i < H; i++) {
                char[][] next = reverseRow(cur, i);
                String encoded = encode(next);
                if (!states.contains(encoded)) {
                    states.add(encoded);
                    q.offer(new Pair(next));
                }
            }

            // Column reverse
            for (int j = 0; j < W; j++) {
                char[][] next = reverseCol(cur, j);
                String encoded = encode(next);
                if (!states.contains(encoded)) {
                    states.add(encoded);
                    q.offer(new Pair(next));
                }
            }
        }
        System.out.println(states.size());
    }

    static char[][] reverseRow(char[][] grid, int row) {
        char[][] newGrid = new char[grid.length][grid[0].length];
        for (int i = 0; i < grid.length; i++) {
            System.arraycopy(grid[i], 0, newGrid[i], 0, grid[0].length);
        }
        for (int i = 0; i < grid[row].length / 2; i++) {
            char temp = newGrid[row][i];
            newGrid[row][i] = newGrid[row][grid[row].length - 1 - i];
            newGrid[row][grid[row].length - 1 - i] = temp;
        }
        return newGrid;
    }

    static char[][] reverseCol(char[][] grid, int col) {
        char[][] newGrid = new char[grid.length][grid[0].length];
        for (int i = 0; i < grid.length; i++) {
            System.arraycopy(grid[i], 0, newGrid[i], 0, grid[0].length);
        }
        for (int i = 0; i < grid.length / 2; i++) {
            char temp = newGrid[i][col];
            newGrid[i][col] = newGrid[grid.length - 1 - i][col];
            newGrid[grid.length - 1 - i][col] = temp;
        }
        return newGrid;
    }

    static String encode(char[][] grid) {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {
                sb.append(grid[i][j]);
            }
        }
        return sb.toString();
    }

    static class Pair {
        char[][] grid;

        Pair(char[][] grid) {
            this.grid = grid;
        }
    }
}

MOD = 1000000007

def solve():
    h, w = map(int, input().split())
    grid = [input() for _ in range(h)]

    rows = ["".join(sorted(row)) for row in grid]
    cols = ["".join(sorted(grid[i][j] for i in range(h))) for j in range(w)]

    row_count = {}
    for row in rows:
        row_count[row] = row_count.get(row, 0) + 1

    col_count = {}
    for col in cols:
        col_count[col] = col_count.get(col, 0) + 1

    ans = 1
    for count in row_count.values():
        ans = (ans * count) % MOD
    for count in col_count.values():
        ans = (ans * count) % MOD

    print(ans)

solve()

