#define H 10
#define W 10

int board[H][W];
int temp_board[H][W];

int main() {
    int h, w;
    scanf("%d %d", &h, &w);

    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            scanf("%d", &board[i][j]);
        }
    }

    long long max_score = -1;
    for (int r = 0; r < h; r++) {
        for (int c = 0; c < w; c++) {
            long long current_score = 0;
            long long power_of_2 = 1;
            int chain_count = 0;

            //create a copy of board
            for(int i=0; i<h; ++i){
                for(int j=0; j<w; ++j){
                    temp_board[i][j] = board[i][j];
                }
            }
            temp_board[r][c] = 0;


            while (1) {
                int disappeared = 0;
                int sum_disappeared = 0;

                //check horizontal
                for (int i = 0; i < h; i++) {
                    for (int j = 0; j < w; j++) {
                        if (temp_board[i][j] != 0) {
                            int count = 1;
                            int k = j + 1;
                            while (k < w && temp_board[i][k] == temp_board[i][j]) {
                                count++;
                                k++;
                            }
                            if (count >= 3) {
                                for (int l = j; l < k; l++) {
                                    sum_disappeared += temp_board[i][l];
                                    temp_board[i][l] = 0;
                                    disappeared = 1;
                                }
                            }
                        }
                    }
                }

                if (disappeared) {
                    current_score += power_of_2 * sum_disappeared;
                    power_of_2 *= 2;
                    chain_count++;

                    //drop stones
                    for (int j = 0; j < w; j++) {
                        int empty_row = h - 1;
                        for (int i = h - 1; i >= 0; i--) {
                            if (temp_board[i][j] != 0) {
                                temp_board[empty_row][j] = temp_board[i][j];
                                if (empty_row != i) temp_board[i][j] = 0;
                                empty_row--;
                            }
                        }
                    }
                } else {
                    break;
                }
            }
            if (current_score > max_score) {
                max_score = current_score;
            }
        }
    }
    printf("%lld\n", max_score);
    return 0;
}

