public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int h = sc.nextInt();
        int w = sc.nextInt();
        int[][] board = new int[h][w];
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                board[i][j] = sc.nextInt();
            }
        }
        long maxScore = -1;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                int[][] tempBoard = new int[h][w];
                for (int k = 0; k < h; k++) {
                    System.arraycopy(board[k], 0, tempBoard[k], 0, w);
                }
                tempBoard[i][j] = 0;
                long score = solve(tempBoard);
                maxScore = Math.max(maxScore, score);
            }
        }
        System.out.println(maxScore);
    }

    public static long solve(int[][] board) {
        int h = board.length;
        int w = board[0].length;
        long score = 0;
        long multiplier = 1;
        while (true) {
            List<int[]> removed = new ArrayList<>();
            for (int i = 0; i < h; i++) {
                int count = 1;
                for (int j = 1; j < w; j++) {
                    if (board[i][j] == board[i][j - 1] && board[i][j] != 0) {
                        count++;
                    } else {
                        if (count >= 3) {
                            for (int k = j - count; k < j; k++) {
                                removed.add(new int[]{i, k});
                            }
                        }
                        count = 1;
                    }
                }
                if (count >= 3) {
                    for (int k = w - count; k < w; k++) {
                        removed.add(new int[]{i, k});
                    }
                }
            }
            if (removed.isEmpty()) break;
            long sum = 0;
            for (int[] pos : removed) {
                sum += board[pos[0]][pos[1]];
                board[pos[0]][pos[1]] = 0;
            }
            score += multiplier * sum;
            multiplier *= 2;
            for (int j = 0; j < w; j++) {
                int k = h - 1;
                for (int i = h - 1; i >= 0; i--) {
                    if (board[i][j] != 0) {
                        board[k][j] = board[i][j];
                        if (i != k) board[i][j] = 0;
                        k--;
                    }
                }
            }
        }
        return score;
    }
}

