def simulate(board):
    score = 0
    total_removed = 0
    while True:
        removed = []
        for r in range(len(board)):
            count = 1
            for c in range(len(board[0]) - 1):
                if board[r][c] == board[r][c+1] and board[r][c] != 0:
                    count += 1
                else:
                    if count >= 3:
                        removed.extend([(r, c-count+1+i) for i in range(count)])
                    count = 1
            if count >= 3:
                removed.extend([(r, len(board[0])-count+i) for i in range(count)])

        if not removed:
            break

        removed_sum = 0
        for r, c in removed:
            removed_sum += board[r][c]
            board[r][c] = 0

        total_removed += removed_sum
        score += total_removed * (2**len(removed))

        #Drop down
        for c in range(len(board[0])):
            new_col = []
            for r in range(len(board)):
                if board[r][c] != 0:
                    new_col.append(board[r][c])
            new_col += [0] * (len(board)-len(new_col))
            for r in range(len(board)):
                board[r][c] = new_col[r]


    return score

