import java.util.*;

public class Main {
    static int n;
    static int[] a;
    static int[] v;
    static List<Integer>[] g;
    static List<Integer> leaves;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        a = new int[n];
        v = new int[n];
        g = new ArrayList[n + 1];
        for (int i = 1; i <= n; i++) {
            g[i] = new ArrayList<>();
        }
        for (int i = 0; i < n - 1; i++) {
            a[i] = sc.nextInt();
            v[i] = sc.nextInt();
            g[a[i]].add(i + 2);
            g[i + 2].add(a[i]);
        }
        leaves = new ArrayList<>();
        dfs(1, -1);
        int m = leaves.size();
        int[] p = new int[m];
        for (int i = 0; i < m; i++) {
            p[i] = i;
        }
        int ans = Integer.MAX_VALUE;
        do {
            int maxCost = 0;
            boolean ok = true;
            List<Integer> path = new ArrayList<>();
            path.add(1);
            for (int i = 0; i < m; i++) {
                int leaf = leaves.get(p[i]);
                List<Integer> curPath = getPath(1, leaf);
                path.addAll(curPath.subList(1, curPath.size()));
            }
            path.add(1);
            int curCost = 0;
            List<Integer> edges = new ArrayList<>();
            for (int i = 0; i < path.size() - 1; i++) {
                int u = path.get(i);
                int next = path.get(i + 1);
                int edgeIndex = -1;
                for (int j = 0; j < g[u].size(); j++) {
                    if (g[u].get(j) == next) {
                        edgeIndex = g[u].get(j) -1;
                        break;
                    }
                }
                if (edgeIndex == -1) {
                    for (int j = 0; j < g[next].size(); j++) {
                        if (g[next].get(j) == u) {
                            edgeIndex = g[next].get(j) -1;
                            break;
                        }
                    }
                }
                curCost += v[edgeIndex];
                edges.add(edgeIndex);

            }
            int[] edgeCount = new int[n-1];
            for (int edge : edges) {
                edgeCount[edge]++;
            }
            for (int count : edgeCount) {
                if (count != 2) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                int dayCost = 0;
                int day = 0;
                int maxDayCost = 0;
                for (int i = 0; i < edges.size(); i++) {
                    dayCost += v[edges.get(i)];
                    if (path.get(i+1) == 1) {
                        maxDayCost = Math.max(maxDayCost, dayCost);
                        dayCost = 0;
                    }
                }
                ans = Math.min(ans, maxDayCost);
            }
        } while (nextPermutation(p));
        System.out.println(ans);
    }

    static void dfs(int u, int p) {
        boolean isLeaf = true;
        for (int v : g[u]) {
            if (v != p) {
                isLeaf = false;
                dfs(v, u);
            }
        }
        if (isLeaf) {
            leaves.add(u);
        }
    }

    static List<Integer> getPath(int start, int end) {
        List<Integer> path = new ArrayList<>();
        Queue<Integer> q = new LinkedList<>();
        Map<Integer, Integer> parent = new HashMap<>();
        q.offer(start);
        parent.put(start, -1);
        while (!q.isEmpty()) {
            int u = q.poll();
            if (u == end) {
                int cur = end;
                while (cur != -1) {
                    path.add(cur);
                    cur = parent.get(cur);
                }
                Collections.reverse(path);
                return path;
            }
            for (int v : g[u]) {
                if (!parent.containsKey(v)) {
                    parent.put(v, u);
                    q.offer(v);
                }
            }
        }
        return path;
    }

    static boolean nextPermutation(int[] p) {
        int i = p.length - 2;
        while (i >= 0 && p[i] >= p[i + 1]) {
            i--;
        }
        if (i < 0) {
            return false;
        }
        int j = p.length - 1;
        while (p[j] <= p[i]) {
            j--;
        }
        swap(p, i, j);
        reverse(p, i + 1);
        return true;
    }

    static void swap(int[] p, int i, int j) {
        int temp = p[i];
        p[i] = p[j];
        p[j] = temp;
    }

    static void reverse(int[] p, int start) {
        int end = p.length - 1;
        while (start < end) {
            swap(p, start, end);
            start++;
            end--;
        }
    }
}

