int main() {
    int n;
    cin >> n;

    vector<tuple<int, int>> edges(n - 1);
    for (int i = 0; i < n - 1; ++i) {
        cin >> get<0>(edges[i]) >> get<1>(edges[i]);
        get<0>(edges[i])--;
        get<1>(edges[i])--;
    }

    int k;
    cin >> k;

    map<int, int> initial_values;
    for (int i = 0; i < k; ++i) {
        int v, p;
        cin >> v >> p;
        initial_values[v - 1] = p;
    }

    vector<int> values(n, -1e9);
    for (auto const& [key, val] : initial_values) {
        values[key] = val;
    }

    vector<vector<int>> adj(n);
    for (const auto& edge : edges) {
        adj[get<0>(edge)].push_back(get<1>(edge));
        adj[get<1>(edge)].push_back(get<0>(edge));
    }

    vector<bool> visited(n, false);
    vector<int> q;
    for (auto const& [key, val] : initial_values) {
        q.push_back(key);
        visited[key] = true;
    }

    int head = 0;
    bool possible = true;
    while (head < q.size()) {
        int u = q[head++];
        for (int v : adj[u]) {
            if (!visited[v]) {
                values[v] = values[u] + 1;
                bool ok = true;
                for (int w : adj[v]) {
                  if (values[w] != -1e9 && abs(values[v] - values[w]) != 1) ok = false;
                }
                if (ok) {
                    visited[v] = true;
                    q.push_back(v);
                } else {
                    possible = false;
                    break;
                }
            } else {
                if (abs(values[u] - values[v]) != 1) {
                    possible = false;
                    break;
                }
            }
            if (!possible) break;
        }
        if (!possible) break;
    }

    if (possible) {
        cout << "Yes" << endl;
        for (int val : values) {
            cout << val << endl;
        }
    } else {
        cout << "No" << endl;
    }

    return 0;
}

