import collections

def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))
    k = int(input())
    assigned = {}
    for _ in range(k):
        v, p = map(int, input().split())
        assigned[v - 1] = p

    graph = collections.defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    result = [-1] * n
    for node, val in assigned.items():
        result[node] = val

    queue = collections.deque(assigned.keys())
    visited = set(assigned.keys())

    while queue:
        curr = queue.popleft()
        for neighbor in graph[curr]:
            if neighbor not in visited:
                result[neighbor] = result[curr] + 1 if result[curr] + 1 - result[neighbor] == 1 else result[curr] -1
                visited.add(neighbor)
                queue.append(neighbor)

    valid = True
    for u, v in edges:
        if abs(result[u] - result[v]) != 1:
            valid = False
            break

    if valid:
        print("Yes")
        for val in result:
            print(val)
    else:
        print("No")

