            changed = false;
            Set<Pair> newBlackCells = new HashSet<>(blackCells);
            for (Pair p1 : blackCells) {
                for (Pair p2 : blackCells) {
                    if (p1.second == p2.first && !blackCells.contains(new Pair(p2.second, p1.first))) {
                        newBlackCells.add(new Pair(p2.second, p1.first));
                        changed = true;
                    }
                }
            }
            blackCells = newBlackCells;
        }
        System.out.println(blackCells.size());
    }
    static class Pair {
        int first;
        int second;
        Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }
        @Override
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (obj == null || getClass() != obj.getClass()) return false;
            Pair pair = (Pair) obj;
            return first == pair.first && second == pair.second;
        }
        @Override
        public int hashCode() {
            return Objects.hash(first, second);
        }
    }
}

