public class Main {
    static final long MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] p = new int[n];
        List<Integer> unknown = new ArrayList<>();
        Set<Integer> known = new HashSet<>();
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextInt();
            if (p[i] == 0) {
                unknown.add(i);
            } else {
                known.add(p[i]);
            }
        }

        int[] unknownArr = new int[unknown.size()];
        for(int i=0; i<unknown.size(); ++i){
            unknownArr[i] = i;
        }

        long ans = 0;
        List<Integer> nums = new ArrayList<>();
        for(int i=1; i<=n; ++i){
            if(!known.contains(i)){
                nums.add(i);
            }
        }


        do{
            long cur = 1;
            int[] perm = new int[n];
            int k=0;
            for(int i=0; i<n; ++i){
                if(p[i] == 0){
                    perm[i] = nums.get(unknownArr[k++]);
                }else{
                    perm[i] = p[i];
                }
            }

            long fact = 1;
            for(int i=1; i<=n; ++i) fact = (fact * i) % MOD;

            for(int i=0; i<n; ++i){
                long count = 0;
                for(int j=1; j<perm[i]; ++j){
                    boolean used = false;
                    for(int l=0; l<=i; ++l){
                        if(perm[l] == j){
                            used = true;
                            break;
                        }
                    }
                    if(!used) count++;
                }
                cur = (cur + count * factorial(n-1-i)) % MOD;

            }
            ans = (ans + cur + 1) % MOD;

        }while(nextPermutation(unknownArr));

        System.out.println(ans);
        sc.close();
    }

    static long factorial(int n) {
        long res = 1;
        for (int i = 2; i <= n; i++)
            res = (res * i) % MOD;
        return res;
    }

    static boolean nextPermutation(int[] arr) {
        int i = arr.length - 2;
        while (i >= 0 && arr[i] >= arr[i + 1]) {
            i--;
        }
        if (i < 0) {
            return false;
        }
        int j = arr.length - 1;
        while (arr[j] <= arr[i]) {
            j--;
        }
        swap(arr, i, j);
        reverse(arr, i + 1, arr.length - 1);
        return true;
    }

    static void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }

    static void reverse(int[] arr, int i, int j) {
        while (i < j) {
            swap(arr, i, j);
            i++;
            j--;
        }
    }
}

