                    if 0 <= nr < h and 0 <= nc < w and temp_grid[nr][nc] != '#' and (nr, nc) not in visited:
                        q.append((nr, nc))
            if escaped:
                break
        if not escaped:
            min_fences = min(min_fences, fence_count)

