using namespace std;

int eval(const string& s, int& pos) {
    if (isdigit(s[pos])) {
        int num = 0;
        while (pos < s.length() && isdigit(s[pos])) {
            num = num * 10 + (s[pos] - '0');
            pos++;
        }
        return num;
    } else {
        char op = s[pos++];
        int left = eval(s, pos);
        pos++; 
        int right = eval(s, pos);
        pos++; 
        if (op == '^') return max(left, right);
        else return min(left, right);
    }
}

