MOD = 10**9 + 7

def solve():
    n, k = map(int, input().split())
    a = [int(input()) for _ in range(n)]

    adj = [[] for _ in range(n)]
    for i in range(n):
        adj[i].append(a[i] - 1)
        adj[a[i]-1].append(i)

    visited = [False] * n
    cycles = []

    def dfs(u, path):
        visited[u] = True
        path.append(u)
        for v in adj[u]:
            if v in path:
                if v == path[0]:
                    cycles.append(path)

            elif not visited[v]:
                dfs(v, path.copy())

    for i in range(n):
        if not visited[i]:
            dfs(i, [])

    ans = 0

    def count_ways(cycle_len, num_colors):

        dp = {}
        def helper(index, prev_color):
            if index == cycle_len:
                return 1

            if (index, prev_color) in dp:
                return dp[(index, prev_color)]

            count = 0
            for color in range(1, num_colors + 1):
                if color != prev_color:
                    count = (count + helper(index + 1, color)) % MOD

            dp[(index, prev_color)] = count
            return count

        total_ways = 0
        for color in range(1, num_colors +1):
            total_ways = (total_ways + helper(1, color)) % MOD
        return total_ways



    total_ways = 1
    for cycle in cycles:
      total_ways = (total_ways * count_ways(len(cycle),k)) % MOD


    print(total_ways)


