                if ((i >> j) % 2 == 1) {
                    if(root == -1) root = j;
                    q.add(j);
                    subtree.add(j);

                }
            }
            if(root == -1) continue;
            Set<Integer> visited = new HashSet<>();
            while(!q.isEmpty()){
                int u = q.poll();
                visited.add(u);
                for(int v: adj.get(u)){
                    if(!visited.contains(v) && subtree.contains(v)){
                        q.add(v);
                        visited.add(v);
                    }
                }
            }

            boolean containsAll = true;
            for (int node : s) {
                if (!subtree.contains(node)) {
                    containsAll = false;
                    break;
                }
            }
            if (containsAll) {
                minSize = Math.min(minSize, subtree.size());
            }
        }
        return minSize;
    }
}

