    scanf("%s %lld", s, &K);
    long long n = strlen(s);
    for (long long i = 0; i < n; i++) {
        long long diff = 'z' - s[i];
        if (K <= 0) break;
        if (K <= diff) {
            s[i] += K;
            K = 0;
        } else {
            s[i] = 'a';
            K -= (diff + 1);
        }
    }
    if (K > 0) {
        long long rem = K % n;
        long long div = K / n;
        for (long long i = 0; i < n; i++) {
            s[i] = 'a' + (s[i] - 'a' + div) % 26;

        }
        for (long long i = 0; i < rem; i++) {
            s[i]++;
            if (s[i] > 'z') s[i] = 'a';
        }
    }
    printf("%s\n", s);
    return 0;
}

