long long calculate(char *s) {
    long long res = 0;
    long long num = 0;
    char op = '+';
    for (int i = 0; i < strlen(s); i++) {
        if (s[i] == '+') {
            if (op == '+') res += num;
            else res -= num;
            num = 0;
            op = '+';
        } else if (s[i] == '-') {
            if (op == '+') res += num;
            else res -= num;
            num = 0;
            op = '-';
        } else {
            num = num * 10 + (s[i] - '0');
        }
    }
    if (op == '+') res += num;
    else res -= num;
    return res;
}

