long long calculate(char *s) {
    long long res = 0;
    long long num = 0;
    int i = 0;
    while (s[i] != '\0') {
        if (s[i] == '+') {
            res += num;
            num = 0;
        } else {
            num = num * 10 + (s[i] - '0');
        }
        i++;
    }
    res += num;
    return res;
}

