long long eval(string s) {
  long long res = 0;
  long long cur = 0;
  for (char c : s) {
    if (isdigit(c)) {
      cur = cur * 10 + (c - '0');
    } else {
      res += cur;
      cur = 0;
    }
  }
  res += cur;
  return res;
}

