using namespace std;

int main() {
  long long n, x;
  cin >> n >> x;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long min_time = -1;

  for (int i = 0; i < (1 << n); ++i) {
    long long current_time = 0;
    vector<bool> has_color(n, false);
    vector<int> current_slimes;

    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        current_time += a[j];
        has_color[j] = true;
        current_slimes.push_back(j);
      }
    }

    bool all_colors = true;
    for (int j = 0; j < n; ++j) {
      if (!has_color[j]) all_colors = false;
    }

    if (all_colors) {
      if (min_time == -1 || current_time < min_time) {
        min_time = current_time;
      }
      continue;
    }

    long long num_spells = 0;
    vector<bool> collected(n,false);

    while(true){
        bool all_collected = true;
        for(int k=0; k<n; ++k) if(!collected[k]) all_collected = false;
        if(all_collected) break;

        long long min_add_time = -1;
        int min_add_index = -1;
        for(int k=0; k<n; ++k){
            bool already_collected = false;
            for(int s : current_slimes) if(s == k) already_collected = true;
            if(!collected[k] && !already_collected){
                if(min_add_time == -1 || a[k] < min_add_time){
                    min_add_time = a[k];
                    min_add_index = k;
                }
            }
        }
        if(min_add_index != -1){
            current_time += min_add_time;
            current_slimes.push_back(min_add_index);
            collected[min_add_index] = true;
        } else {
            current_time += x;
            vector<int> next_slimes;
            for(int s : current_slimes){
                next_slimes.push_back((s+1)%n);
            }
            current_slimes = next_slimes;
            num_spells++;
        }
    }
    if (min_time == -1 || current_time < min_time) {
      min_time = current_time;
    }
  }

  cout << min_time << endl;
  return 0;
}

