#define MAXN 100005

int n, m;
int a[MAXN], b[MAXN];
int color[MAXN];
int visited[MAXN];
int graph[MAXN][MAXN];
int dist[MAXN];

int dfs(int u, int p, int c) {
    visited[u] = 1;
    color[u] = c;
    int cnt = 0;
    for (int v = 1; v <= n; v++) {
        if (graph[u][v] && v != p) {
            if (visited[v]) {
                if (color[u] == color[v]) return -1;
            } else {
                int res = dfs(v, u, 1 - c);
                if (res == -1) return -1;
                cnt += res;
            }
        }
    }
    return cnt + c;
}

