public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        int[] color = new int[n];
        int ans = Integer.MAX_VALUE;
        for (int i = 0; i < (1 << n); i++) {
            int cnt = 0;
            int[] cur_color = Arrays.copyOf(color, n);
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    cur_color[j] = 1;
                }
            }
            boolean flag = true;
            Queue<Integer> q = new LinkedList<>();
            for (int j = 0; j < n; j++) {
                if (cur_color[j] == 0) {
                    q.offer(j);
                }
            }
            while (!q.isEmpty()) {
                int u = q.poll();
                if (cur_color[u] == 1) continue;
                for (int v : adj.get(u)) {
                    if (cur_color[u] == cur_color[v]) {
                        cur_color[u] = 1 - cur_color[u];
                        cur_color[v] = 1 - cur_color[v];
                        cnt++;
                        if(cur_color[u] == 0) q.offer(u);
                        if(cur_color[v] == 0) q.offer(v);
                    }
                }
            }
            boolean all_black = true;
            for (int j = 0; j < n; j++) {
                if (cur_color[j] == 0) {
                    all_black = false;
                    break;
                }
            }
            if (all_black) {
                ans = Math.min(ans, cnt);
            }
        }
        if (ans == Integer.MAX_VALUE) {
            System.out.println(-1);
        } else {
            System.out.println(ans);
        }
    }
}

