long long f(long long b, long long n) {
    if (n < b) return n;
    return f(b, n / b) + n % b;
}

int main() {
    long long n, s;
    scanf("%lld %lld", &n, &s);
    if (n < s) {
        printf("-1\n");
        return 0;
    }
    if (n == s) {
        printf("%lld\n", n);
        return 0;
    }

    for (long long b = 2; b <= 100000; ++b) {
        if (f(b, n) == s) {
            printf("%lld\n", b);
            return 0;
        }
    }
    if (n < s)
        printf("-1\n");
    else {
        for (long long b = 2; b <= n; b++) {
            if (f(b, n) == s) {
                printf("%lld\n", b);
                return 0;
            }
        }
        printf("-1\n");
