#define MOD 1000000007

int isGood(char *s) {
    int len = strlen(s);
    for (int k = 2; k <= len; ++k) {
        for (int i = 0; i <= len - k; ++i) {
            char *sub = (char *)malloc((k + 1) * sizeof(char));
            strncpy(sub, s + i, k);
            sub[k] = '\0';
            char *rep = (char *)malloc((len + 1) * sizeof(char));
            for (int j = 0; j < len / k; ++j) {
                strcat(rep, sub);
            }
            if (strcmp(s, rep) == 0) {
                free(sub);
                free(rep);
                return 0;
            }
            free(sub);
            free(rep);
        }
    }
    return 1;
}

int main() {
    char w[500001];
    scanf("%s", w);
    int len = strlen(w);
    int minLen = len;
    long long count = 0;
    for (int i = 0; i < (1 << (len - 1)); ++i) {
        int numParts = 0;
        int start = 0;
        char **parts = (char **)malloc(len * sizeof(char *));
        for (int j = 0; j < len - 1; ++j) {
            if ((i >> j) & 1) {
                numParts++;
                parts[numParts -1] = (char *)malloc((j - start + 2) * sizeof(char));
                strncpy(parts[numParts - 1], w + start, j - start + 1);
                parts[numParts - 1][j - start + 1] = '\0';
                start = j + 1;
            }
        }
        numParts++;
        parts[numParts - 1] = (char *)malloc((len - start + 1) * sizeof(char));
        strncpy(parts[numParts - 1], w + start, len - start);
        parts[numParts - 1][len - start] = '\0';

        int good = 1;
        for (int j = 0; j < numParts; ++j) {
            if (!isGood(parts[j])) {
                good = 0;
                break;
            }
        }
        if (good) {
            if (numParts < minLen) {
                minLen = numParts;
                count = 1;
            } else if (numParts == minLen) {
                count++;
            }
        }
        for(int k=0; k< numParts; ++k) free(parts[k]);
        free(parts);
    }
    printf("%d\n%lld\n", minLen, count % MOD);
    return 0;
}

