#include <math.h>

long long cube_root(long long n) {
    long long lo = 0, hi = 1000000;
    long long ans = 0;
    while (lo <= hi) {
        long long mid = lo + (hi - lo) / 2;
        long long cube = mid * mid * mid;
        if (cube == n) {
            return mid;
        } else if (cube < n) {
            ans = mid;
            lo = mid + 1;
        } else {
            hi = mid - 1;
        }
    }
    return ans;
}

long long max_power(long long n) {
    long long count = 0;
    for (long long i = 2; i * i * i <= n; ++i) {
        while (n % i == 0) {
            n /= i;
            count++;
        }
    }
    if (n > 1) {
        count++;
    }
    return count;
}


