long long cube_free(long long n) {
    long long res = 1;
    for (long long i = 2; i * i * i <= n; ++i) {
        long long cnt = 0;
        while (n % i == 0) {
            n /= i;
            cnt++;
        }
        if (cnt % 3 != 0) res *= i;
    }
    if (n > 1) res *= n;
    return res;
}

