            dp[i][j] = 0;
        }
    }
    dp[0][0] = 1;
    for (int i = 0; i < N; i++) {
        for (int j = C; j >= 0; j--) {
            for (int k = 0; k <= j && k <= B[i]; k++) {
                dp[j][i + 1] = (dp[j][i + 1] + dp[j - k][i]) % MOD;
            }
        }
    }

    for (int i = 0; i <= C; i++) {
      long long cur = dp[i][N];
      long long prod = 1;
      for (int j = 0; j < N; j++){
        long long temp = 1;
        for (int k = 0; k < i; k++){
          temp = (temp * A[j]) % MOD;
        }
        prod = (prod * temp) % MOD;
      }
      ans = (ans + prod * cur) % MOD;
    }
