        }

        long ans = 0;
        int[] candies = new int[n];
        dfs(n, c, 0, candies, a, b, ans);

        System.out.println(ans);
    }

    static void dfs(int n, int c, int k, int[] candies, int[] a, int[] b, long ans) {
        if (k == n) {
            int sum = 0;
            for (int candy : candies) {
                sum += candy;
            }
            if (sum == c) {
                long activity = 1;
                for (int i = 0; i < n; i++) {
                    long power = 1;
                    for (int j = 0; j < candies[i]; j++) {
                        power = (power * a[i]) % MOD;
                    }
                    activity = (activity * power) % MOD;

                }
                ans = (ans + activity) % MOD;
            }
            return;
        }
        for (int i = 0; i <= c; i++) {
            candies[k] = i;
            dfs(n, c, k + 1, candies, a, b, ans);
        }
    }
}

