using namespace std;

long long combinations(int n, int k, const vector<long long>& fact, const vector<long long>& inv) {
  if (k < 0 || k > n) return 0;
  return fact[n] * inv[k] % 1000000007 * inv[n - k] % 1000000007;
}

long long power(long long base, long long exp) {
  long long res = 1;
  base %= 1000000007;
  while (exp > 0) {
    if (exp % 2 == 1) res = (res * base) % 1000000007;
    base = (base * base) % 1000000007;
    exp /= 2;
  }
  return res;
}

long long inverse(long long n) {
  return power(n, 1000000007 - 2);
}

int main() {
  int n, k;
  cin >> n >> k;

  vector<long long> fact(n * k + 1);
  vector<long long> inv(n * k + 1);
  fact[0] = 1;
  inv[0] = 1;
  for (int i = 1; i <= n * k; ++i) {
    fact[i] = (fact[i - 1] * i) % 1000000007;
    inv[i] = inverse(fact[i]);
  }

  long long ans = fact[n * k] * inv[k] % 1000000007;
  for (int i = 0; i < n; ++i) {
    ans = (ans * inv[k]) % 1000000007;
  }


  long long total_arrangements = fact[n*k];
  for (int i = 0; i < n; ++i) {
    total_arrangements = (total_arrangements * inv[k]) % 1000000007;
  }

