    inv[0] = 1;
    for (int i = 1; i <= H + W; i++) {
        fact[i] = (fact[i - 1] * i) % MOD;
        inv[i] = inverse(fact[i]);
    }

    long long total_paths = combinations(H + W - 2, H - 1, fact, inv);
    long long forbidden_paths = combinations(H - 1 + W -1 -A-B , H -A-1, fact, inv);
    long long result = (total_paths - forbidden_paths + MOD) % MOD;

    printf("%lld\n", result);
    return 0;
}

