    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= k; j++) {
            dp[i][j] = -1;
        }
    }
    dp[0][0] = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= k; j++) {
            if (dp[i][j] != -1) {
                if (j + len[i] <= k) {
                    if (dp[i + 1][j + len[i]] == -1 || strcmp(ans, strcat(ans, s[i])) < 0) {
                        strcpy(ans, "");
                        strcat(ans, s[i]);
                        dp[i + 1][j + len[i]] = 1;
                    }
                }
            }
        }
    }
    char *res = (char *)malloc((k + 1) * sizeof(char));
    strcpy(res, "");
    int cur = k;
