void addedge(int u, int v) {
    nxt[edge_cnt] = head[u];
    to[edge_cnt] = v;
    head[u] = edge_cnt++;
    nxt[edge_cnt] = head[v];
    to[edge_cnt] = u;
    head[v] = edge_cnt++;
}

void bfs(int s, int d[]) {
    for (int i = 1; i <= n; i++) d[i] = -1;
    d[s] = 0;
    int q[MAXN], head_q = 0, tail_q = 0;
    q[tail_q++] = s;
    while (head_q < tail_q) {
        int u = q[head_q++];
        for (int i = head[u]; i != -1; i = nxt[i]) {
            int v = to[i];
            if (d[v] == -1) {
                d[v] = d[u] + 1;
                q[tail_q++] = v;
            }
        }
    }
}

