public class Main {
    static int n, k;
    static ArrayList<Integer>[] adj;
    static int[] parent;
    static int maxDist;
    static int farthestNode;

    static void dfs(int u, int p, int dist) {
        if (dist > maxDist) {
            maxDist = dist;
            farthestNode = u;
        }
        parent[u] = p;
        for (int v : adj[u]) {
            if (v != p) {
                dfs(v, u, dist + 1);
            }
        }
    }

    static int getDiameter(int u) {
        maxDist = 0;
        farthestNode = u;
        dfs(u, -1, 0);
        maxDist = 0;
        dfs(farthestNode, -1, 0);
        return maxDist;
    }

    static int solve(int mask) {
        ArrayList<Integer> nodes = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            if ((mask >> i & 1) == 1) {
                nodes.add(i + 1);
            }
        }
        if (nodes.size() == 0) return n;

        ArrayList<Integer>[] newAdj = new ArrayList[n + 1];
        for (int i = 1; i <= n; i++) {
            newAdj[i] = new ArrayList<>();
        }
        for (int i = 1; i <= n; i++) {
            for (int j : adj[i]) {
                boolean ok = true;
                if (nodes.contains(i) && nodes.contains(j)) {
                    newAdj[i].add(j);
                    newAdj[j].add(i);
                }
            }
        }
        if(nodes.size() == 1) return n-1;

        boolean connected = true;
        boolean[] visited = new boolean[n + 1];
        Queue<Integer> q = new LinkedList<>();
        q.add(nodes.get(0));
        visited[nodes.get(0)] = true;
        int count = 0;
        while (!q.isEmpty()) {
            int u = q.poll();
            count++;
            for (int v : newAdj[u]) {
                if (!visited[v] && nodes.contains(v)) {
                    visited[v] = true;
                    q.add(v);
                }
            }
        }

        for (int u : nodes) {
            if (!visited[u]) connected = false;
        }

        if (!connected) return n;

        if (getDiameter(nodes.get(0)) <= k) {
            return n - nodes.size();
        } else {
            return n;
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        k = sc.nextInt();
        adj = new ArrayList[n + 1];
        for (int i = 1; i <= n; i++) {
            adj[i] = new ArrayList<>();
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt();
