            adj[u].add(v);
            adj[v].add(u);
        }
        parent = new int[n + 1];
        int ans = n;
        for (int i = 0; i < (1 << n); i++) {
            ans = Math.min(ans, solve(i));
        }
        System.out.println(ans);
    }
}

