#include <math.h>

#define N 2000

int main() {
    int n, z, w, i, a[N], max, min, sum, ans;

    scanf("%d %d %d", &n, &z, &w);
    for (i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }

    max = 0;
    min = 0;
    sum = 0;
    for (i = 0; i < n; i++) {
        sum += a[i];
        if (i < n - 1) {
            max = max > sum - a[i] ? max : sum - a[i];
            min = min < sum - a[i] ? min : sum - a[i];
        }
    }

    ans = max - min;
    if (z > w) {
        ans = max - (sum - a[n - 1]);
    } else if (z < w) {
        ans = (sum - a[n - 1]) - min;
    }

    printf("%d\n", ans);

    return 0;
}
