import math

def prime_factors(n):
    i = 2
    factors = set()
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.add(i)
    if n > 1:
        factors.add(n)
    return factors

def max_earnings(N, A):
    factors = set()
    for i in range(1, N + 1):
        factors.update(prime_factors(i))
    factors = sorted(list(factors))
    dp = [0] * (N + 1)
    for i in range(1, N + 1):
        dp[i] = max(dp[i - 1], A[i - 1] + dp[i - 1 - 1])
        for f in factors:
            if i % f == 0:
                dp[i] = max(dp[i], A[i - 1] + dp[i - 1 - f])
    return dp[N]

N = int(input())
A = list(map(int, input().split()))
print(max_earnings(N, A))
