#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 200000
#define Q 200000

int n, q;
int b[N + 1];
int l[Q + 1], r[Q + 1];
int cnt[N + 1];
int seg[N * 4 + 1];

void update(int k, int l, int r, int x, int y) {
    if (l == r) {
        seg[k] = y;
        return;
    }
    int m = (l + r) / 2;
    if (x <= m) update(k * 2, l, m, x, y);
    else update(k * 2 + 1, m + 1, r, x, y);
    seg[k] = seg[k * 2] + seg[k * 2 + 1];
}

int query(int k, int l, int r, int x, int y) {
    if (x <= l && r <= y) return seg[k];
    int m = (l + r) / 2;
    int ret = 0;
    if (x <= m) ret += query(k * 2, l, m, x, y);
    if (y > m) ret += query(k * 2 + 1, m + 1, r, x, y);
    return ret;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) scanf("%d", &b[i]);
    scanf("%d", &q);
    for (int i = 1; i <= q; i++) scanf("%d %d", &l[i], &r[i]);
    for (int i = 1; i <= n; i++) {
        cnt[i] = cnt[i - 1] + (b[i] == 0);
        update(1, 1, n, i, b[i]);
    }
    int ans = 0;
    for (int i = 1; i <= q; i++) {
        int len = r[i] - l[i] + 1;
        int val = query(1, 1, n, l[i], r[i]);
        ans = std::min(ans, cnt[n] - cnt[r[i]] + val + len - val);
        update(1, 1, n, l[i], 1);
    }
    printf("%d\n", n - ans);
    return 0;
}
