```java
import java.io.*;
import java.util.*;

public class Main {
    static class SegmentTree {
        int n;
        int[] tree;
        int[] lazy;

        public SegmentTree(int n) {
            this.n = n;
            tree = new int[4 * n];
            lazy = new int[4 * n];
        }

        void push(int v, int tl, int tr) {
            if (lazy[v] != 0) {
                tree[v] = (tr - tl + 1) * lazy[v];
                if (tl != tr) {
                    lazy[2 * v] = lazy[v];
                    lazy[2 * v + 1] = lazy[v];
                }
                lazy[v] = 0;
            }
        }

        void update(int v, int tl, int tr, int l, int r, int val) {
            push(v, tl, tr);
            if (l > r) return;
            if (l == tl && tr == r) {
                lazy[v] = val;
                push(v, tl, tr);
                return;
            }
            int tm = (tl + tr) / 2;
            update(2 * v, tl, tm, l, Math.min(r, tm), val);
            update(2 * v + 1, tm + 1, tr, Math.max(l, tm + 1), r, val);
            tree[v] = tree[2 * v] + tree[2 * v + 1];
        }

        int query(int v, int tl, int tr, int l, int r) {
            push(v, tl, tr);
            if (l > r) return 0;
            if (l == tl && tr == r) return tree[v];
            int tm = (tl + tr) / 2;
            return query(2 * v, tl, tm, l, Math.min(r, tm)) +
                    query(2 * v + 1, tm + 1, tr, Math.max(l, tm + 1), r);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int[] b = new int[n];
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < n; i++) {
            b[i] = Integer.parseInt(st.nextToken());
        }
        int q = Integer.parseInt(br.readLine());
        int[][] ops = new int[q][2];
        for (int i = 0; i < q; i++) {
            st = new StringTokenizer(br.readLine());
            ops[i][0] = Integer.parseInt(st.nextToken()) - 1;
            ops[i][1] = Integer.parseInt(st.nextToken()) - 1;
        }
        Arrays.sort(ops, Comparator.comparingInt(a -> a[0]));
        SegmentTree stree = new SegmentTree(n);
        int ans = 0;
        for (int i = 0; i < q; i++) {
            int l = ops[i][0];
            int r = ops[i][1];
            int cnt = stree.query(1, 0, n - 1, l, r);
            ans += r - l + 1 - cnt;
            stree.update(1, 0, n - 1, l, r, 1);
        }
        pw.println(n - ans);
        pw.close();
    }
}
