#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

typedef struct {
    int a, b, c;
} part;

int cmp(const void *a, const void *b) {
    return ((part *)a)->b - ((part *)b)->b;
}

int main() {
    int n;
    scanf("%d", &n);
    part p[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i].a);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i].b);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i].c);
    }
    qsort(p, n, sizeof(part), cmp);
    long long ans = 0;
    for (int i = 0; i < n; i++) {
        int l = i, r = n - 1;
        while (l < r) {
            int mid = (l + r + 1) / 2;
            if (p[mid].b > p[i].b) {
                l = mid;
            } else {
                r = mid - 1;
            }
        }
        int j = l;
        while (j < n && p[j].b == p[i].b) {
            j++;
        }
        int k = j;
        while (k < n && p[k].c > p[i].c) {
            k++;
        }
        ans += (j - i) * (k - j);
    }
    printf("%lld\n", ans);
    return 0;
}
