#define MAXN 4000

typedef struct {
    int bit;
    int cnt;
} BIT;

BIT bit[MAXN];

void update(int i, int v) {
    for (; i < MAXN; i += i & -i) {
        bit[i].cnt = (bit[i].cnt + v) % MOD;
    }
}

int query(int i) {
    int ret = 0;
    for (; i > 0; i -= i & -i) {
        ret = (ret + bit[i].cnt) % MOD;
    }
    return ret;
}

