        for (int j = 0; j < 20; j++) {
            if (tmp & (1 << j)) {
                update(j + 1, 1);
            }
        }
        for (int j = 0; j < 20; j++) {
            if (tmp & (1 << j)) {
                tmp ^= a[i];
                for (int k = 0; k < 20; k++) {
                    if (tmp & (1 << k)) {
                        update(k + 1, 1);
                    }
                }
            }
        }
        ans = (ans * (query(20) + 1)) % MOD;
