```java
import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static final int MAX = 4000;

    static class BIT {
        int[] bit;

        BIT(int n) {
            bit = new int[n + 1];
        }

        void add(int i, int x) {
            while (i < bit.length) {
                bit[i] = (bit[i] + x) % MOD;
                i += i & -i;
            }
        }

        int sum(int i) {
            int res = 0;
            while (i > 0) {
                res = (res + bit[i]) % MOD;
                i -= i & -i;
            }
            return res;
        }
    }

    static int n, x;
    static int[] a;

    static void solve() throws IOException {
        n = nextInt();
        x = nextInt();
        a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = nextInt();
        }
        int[] cnt = new int[MAX];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < MAX; j++) {
                if ((a[i] & (1 << j)) != 0) {
                    cnt[j]++;
                }
            }
        }
        BIT bit = new BIT(MAX);
        for (int i = 0; i < MAX; i++) {
            bit.add(i, cnt[i]);
        }
        int ans = 1;
        for (int i = 0; i < MAX; i++) {
            if ((x & (1 << i)) != 0) {
                ans = (int) ((long) ans * (bit.sum(i) + 1) % MOD);
            }
            bit.add(i, 1);
        }
        out.println(ans);
    }

    static BufferedReader br;
    static StringTokenizer st;
    static PrintWriter out;

    public static void main(String[] args) throws IOException {
        br = new BufferedReader(new InputStreamReader(System.in));
        out = new PrintWriter(System.out);
        solve();
        out.close();
    }

    static String next() throws IOException {
        while (st == null || !st.hasMoreTokens()) {
            st = new StringTokenizer(br.readLine());
        }
        return st.nextToken();
    }

    static int nextInt() throws IOException {
        return Integer.parseInt(next());
    }
}
