bool check(const string& s, const string& t) {
    int n = s.size();
    int m = t.size();
    for (int i = 0; i <= n - m; i++) {
        bool flag = true;
        for (int j = 0; j < m; j++) {
            if (s[i + j] != '?' && s[i + j] != t[j]) {
                flag = false;
                break;
            }
        }
        if (flag) return true;
    }
    return false;
}

string solve(string s, string t) {
    int n = s.size();
    int m = t.size();
    vector<string> ans;
    for (int i = 0; i <= n - m; i++) {
        string tmp = s;
        for (int j = 0; j < m; j++) {
            if (s[i + j] == '?') tmp[i + j] = t[j];
        }
        ans.push_back(tmp);
    }
    sort(ans.begin(), ans.end());
    for (const auto& x : ans) {
        if (check(x, t)) return x;
    }
    return "UNRESTORABLE";
}

int main() {
    string s, t;
    cin >> s >> t;
    cout << solve(s, t) << endl;
    return 0;
