        for (int i = 1; i <= input[0].length(); i++) {
            for (int j = 1; j <= input[1].length(); j++) {
                if (s[0][i] == '?' || s[0][i] == t[j]) {
                    s[1][j] = s[0][i];
                }
            }
        }
        for (int i = 1; i <= input[1].length(); i++) {
            if (s[1][i] == 0) {
                System.out.println("UNRESTORABLE");
                return;
            }
        }
        for (int i = 1; i <= input[1].length(); i++) {
            ans[i] = s[1][i];
        }
        for (int i = 1; i <= input[1].length(); i++) {
            for (int j = i; j <= input[1].length(); j++) {
                for (int k = 1; k <= 26; k++) {
                    char c = (char) ('a' + k - 1);
                    if (ans[j + 1] == '?') {
                        ans[j + 1] = c;
                        if (check()) {
                            return;
                        }
                        ans[j + 1] = '?';
                    }
                }
            }
        }
        System.out.println("UNRESTORABLE");
    }

    static boolean check() {
        for (int i = 1; i <= t.length; i++) {
            if (t[i] != '?' && t[i] != ans[i]) {
                return false;
            }
        }
        return true;
    }
