int lcm(int a, int b) {
    return (a / gcd(a, b)) * b;
}

int main() {
    int A, B, C, D;
    scanf("%d %d %d %d", &A, &B, &C, &D);

    int L = lcm(lcm(A, B), C);
    int ans = 0;

    for (int x = 0; x <= L; x++) {
        for (int y = 0; y <= L; y++) {
            for (int z = 0; z <= L; z++) {
                int dx = abs(x - A);
                int dy = abs(y - B);
                int dz = abs(z - C);
                if (dx + dy + dz <= D) {
                    int d = max(max(dx, dy), dz);
                    int x1 = x - d;
                    int y1 = y - d;
                    int z1 = z - d;
                    int x2 = x + d;
                    int y2 = y + d;
                    int z2 = z + d;
                    int cnt = 0;
                    for (int i = x1; i <= x2; i++) {
                        for (int j = y1; j <= y2; j++) {
                            for (int k = z1; k <= z2; k++) {
                                if (i >= 0 && i <= A && j >= 0 && j <= B && k >= 0 && k <= C) {
                                    cnt++;
                                }
                            }
                        }
                    }
                    ans = (ans + cnt) % MOD;
                }
            }
        }
    }

