import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAX = 50005;

    static long[] fac = new long[MAX];
    static long[] inv = new long[MAX];

    static long qpow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = res * a % MOD;
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }

    static long C(int n, int m) {
        if (n < m) return 0;
        return fac[n] * inv[m] % MOD * inv[n - m] % MOD;
    }

    static void init() {
        fac[0] = 1;
        for (int i = 1; i < MAX; i++) fac[i] = fac[i - 1] * i % MOD;
        inv[MAX - 1] = qpow(fac[MAX - 1], MOD - 2);
        for (int i = MAX - 2; i >= 0; i--) inv[i] = inv[i + 1] * (i + 1) % MOD;
    }

    static long solve(int a, int b, int c, int d) {
        long res = 0;
        for (int i = 0; i <= d; i++) {
            int x = d - i;
            int y = d - i;
            int z = d - i;
            int l = Math.max(0, x - a);
            int r = Math.min(x, b - 1);
            int t = Math.min(y, c - 1);
            res = (res + C(a + b + c - 3, a - 1) * C(a + b + c - 3, b - 1) % MOD * C(a + b + c - 3, c - 1) % MOD * C(a + b + c - 3, x + y + z - 3) % MOD * C(a + b + c - 3, l) % MOD * C(a + b + c - 3, r - l + 1) % MOD * C(a + b + c - 3, t - r + 1) % MOD * C(a + b + c - 3, t - (r - l + 1) + 1) % MOD) % MOD;
        }
        return res;
    }

    public static void main(String[] args) throws IOException {
        init();
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int a = Integer.parseInt(st.nextToken());
        int b = Integer.parseInt(st.nextToken());
        int c = Integer.parseInt(st.nextToken());
        int d = Integer.parseInt(st.nextToken());
        System.out.println((solve(a, b, c, d) + MOD) % MOD);
    }
}
