#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

typedef struct {
    int a[400];
    int b[400];
    int c[400];
    int n;
} Sushi;

void read_input(Sushi *s) {
    scanf("%d", &s->n);
    for (int i = 0; i < s->n; i++) {
        scanf("%d", &s->a[i]);
    }
    for (int i = 0; i < s->n; i++) {
        scanf("%d", &s->b[i]);
    }
}

void solve(Sushi *s) {
    int n = s->n;
    int a[3] = {0};
    int b[3] = {0};
    int c[3] = {0};
    int cnt = 0;

    for (int i = 0; i < n; i++) {
        a[s->a[i] % 3]++;
        b[s->b[i] % 3]++;
    }

    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            for (int k = 0; k < 3; k++) {
                if (i != j && i != k && j != k) {
                    c[i]++;
                    c[j]++;
                    c[k]++;
                    int flag = 1;
                    for (int l = 0; l < n; l++) {
                        if (s->a[l] % 3 == i && s->b[l] % 3 == j && s->a[l] != s->b[l]) {
                            flag = 0;
                            break;
                        }
                    }
                    if (flag) {
                        cnt++;
                    }
                    c[i]--;
                    c[j]--;
                    c[k]--;
                }
            }
        }
    }

    printf("%d\n", (long long)cnt * factorial(n / 3 - 1, MOD) % MOD);
}

long long factorial(int n, int mod) {
    long long res = 1;
    for (int i = 2; i <= n; i++) {
        res = (res * i) % mod;
    }
    return res;
}

int main() {
    Sushi s;
    read_input(&s);
    solve(&s);
    return 0;
}
