        int x = cnt[i];
        int y = cnt[i + 1];
        int z = cnt[i + 2];

        if (x == 1 && y == 1 && z == 1) {
            ans = (long long)ans * 2 % MOD;
        } else if (x == 2 && y == 2 && z == 2) {
            ans = (long long)ans * 3 % MOD;
        } else if (x == 1 && y == 1 && z == 2) {
            ans = (long long)ans * 2 % MOD;
        } else if (x == 1 && y == 2 && z == 1) {
            ans = (long long)ans * 2 % MOD;
        } else if (x == 2 && y == 1 && z == 1) {
            ans = (long long)ans * 2 % MOD;
        }
    }

    cout << ans << endl;

    return 0;
}
