using namespace std;

const int N = 55;

int n, m;
int low[N], disc[N], vis[N], tim;
vector<int> g[N];
int a[N], b[N];

void dfs(int u, int p) {
    low[u] = disc[u] = ++tim;
    for (int v : g[u]) {
        if (v == p) continue;
        if (!disc[v]) {
            dfs(v, u);
            low[u] = min(low[u], low[v]);
            if (low[v] > disc[u]) a[m] = u, b[m] = v;
        } else {
            low[u] = min(low[u], disc[v]);
        }
    }
}

int main() {
    cin >> n >> m;
    for (int i = 0; i < m; i++) {
        cin >> a[i] >> b[i];
        g[a[i]].push_back(b[i]);
        g[b[i]].push_back(a[i]);
    }
    for (int i = 1; i <= n; i++) {
        if (!disc[i]) dfs(i, 0);
    }
    int ans = 0;
    for (int i = 0; i < m; i++) {
        if (a[i] == b[i]) continue;
        vis[i] = 1;
        for (int j = 0; j < m; j++) {
            if (i == j) continue;
            if (vis[j] || a[j] == a[i] && b[j] == b[i]) continue;
            vis[j] = 1;
            if (g[a[j]].size() == 1 || g[b[j]].size() == 1) continue;
            int cnt = 0;
            for (int k = 0; k < m; k++) {
                if (vis[k]) continue;
                if (a[k] == a[j] || b[k] == b[j]) continue;
                if (a[k] == a[i] || b[k] == b[i]) continue;
                if (a[k] == b[j] || b[k] == a[j]) continue;
                if (a[k] == b[i] || b[k] == a[i]) continue;
                cnt++;
            }
            if (cnt == 0) ans++;
        }
    }
    cout << ans << endl;
    return 0;
}
