const int MAX_N = 100005;

int n, m, cnt, ans;
vector<int> g[MAX_N];
int d[MAX_N];
bool vis[MAX_N];

void bfs(int s) {
    queue<int> q;
    q.push(s);
    vis[s] = true;
    d[s] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : g[u]) {
            if (!vis[v]) {
                vis[v] = true;
                d[v] = d[u] + 1;
                q.push(v);
            }
        }
    }
}

